import styled, { css } from 'styled-components';

const itemDetailsStyles = css`
    width: 23%;
`;

export const CheckoutItemContainer = styled.div`
  width: 100%;
  display: flex;
  min-height: 100%;
  border-bottom: 1px solid darkgrey;
  padding: 15px 0;
  font-size: 20px;
  align-items: center;
`;

export const ImageContainer = styled.div`
    width: 23%;
    padding-right: 15px;

    img{
        width: 100%;
        height: 100%;
    }
`;


export const ItemDetail = styled.span`
    ${itemDetailsStyles}
`;

export const ItemQuantityContainer = styled.div`
    display: flex;
    ${itemDetailsStyles}

    span{
        margin: 0 10px;
    }
    div{
        cursor: pointer;
    }
`;


export const RemoveButtonContainer = styled.div`
    padding-left: 12px;
    cursor: pointer;
`;
